@import "./sub", "./sub2.scss";

$color: royalblue;

.container{
  h1 {
    color: $color;
    background-color: orange;
    font-size: 60px;
    // font-size: 20px;   // css로 변환될 경우 나타나지 않음
    /*color: #000;*/    // css로 변환할 경우 나타남
  }
  ul{
    li{
      font-size: 40px;
      .name{
        color: $color;
      }
      .age{
        color: orange;
      }
    }
  }
}


